workflow_source:
version 1.0

import "examples/common_tasks.wdl" as common

workflow AnalysisWithImport {
  input {
    File raw_data
    Int quality_threshold = 25
  }

  call PrepareData {
    input:
      input_file = raw_data
  }

  call common.QualityCheck {
    input:
      data_file = PrepareData.output_file,
      threshold = quality_threshold
  }

  call common.DataValidation {
    input:
      input_file = PrepareData.output_file
  }

  call FinalizeResults {
    input:
      qc_file = common.QualityCheck.result,
      validation_file = common.DataValidation.count
  }

  output {
    File qc_result = common.QualityCheck.result
    File validation = common.DataValidation.count
    File final_report = FinalizeResults.report
  }
}

task PrepareData {
  input {
    File input_file
  }

  command <<<
    echo "Preparing data from ${input_file}"
    cp ${input_file} prepared_data.txt
  >>>

  output {
    File output_file = "prepared_data.txt"
  }
}

task FinalizeResults {
  input {
    File qc_file
    File validation_file
  }

  command <<<
    echo "Finalizing results"
    cat ${qc_file} ${validation_file} > final_report.txt
  >>>

  output {
    File report = "final_report.txt"
  }
}
