workflow_source:
version 1.0

workflow SimpleTest {
  input {
    String name
  }

  call Hello {
    input: name = name
  }

  output {
    String result = Hello.message
  }
}

task Hello {
  input {
    String name
  }

  command <<<
    echo "Hello ${name}"
  >>>

  output {
    String message = stdout()
  }
}
